import "core-js/modules/es.object.to-string.js";
import { AgGridVue } from "ag-grid-vue3";
import { reactive, h, onMounted } from "vue";
import YearFilterVue from './YearFilter.vue';
export default {
  name: "App",
  components: {
    AgGridVue: AgGridVue,
    YearFilterVue: YearFilterVue
  },
  setup: function setup(props) {
    var rowData = reactive({
      rows: []
    });
    var columnDefs = reactive({
      columns: [{
        field: "athlete"
      }, {
        field: "year",
        filter: YearFilterVue
      }, {
        field: "age"
      }, {
        field: "country"
      }]
    });
    var defaultColDef = reactive({
      def: {
        flex: 1
      }
    });
    onMounted(function () {
      fetch("https://www.ag-grid.com/example-assets/olympic-winners.json").then(function (resp) {
        return resp.json();
      }).then(function (data) {
        return rowData.rows = data;
      });
    });
    return {
      columnDefs: columnDefs,
      rowData: rowData,
      defaultColDef: defaultColDef
    };
  }
};
